(ns sql.budget
  (:use [com.rps.util.dao.cljutil.daoutil2] )
  (:import [javax.servlet.http HttpServletRequest]
           [com.rps.util.dao SqlAndParams]
           [java.util ArrayList]))

(defsql pageBudget {
    :sql "select * from t_b_budget "
    :where (AND 
         {"orgIdList"  "org_id in @orgIdList"}
         {"check_status_not_in"  "check_status != ?"}
         ("department_id"  "department_id = ?")
         {"year_num"  "year_num = ?"}
         ("year_num"  "year_num = ?")
         ("budget_status"  "budget_status = ?")
         ("subject_id"  "subject_id = ?")
     )
    :page true
    :orderby true                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
    :orderby-default " order by department_id asc, subject_id asc"
})

(defsql pageBudgetSpecial {
    :sql "select * from t_b_budget_special "
    :where (AND 
         ("budget_special_name"  "budget_special_name like ?" "%" "%")
     )
    :page true
    :orderby true                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                          
    :orderby-default " order by budget_special_id asc"
})